import React, { Component } from 'react';

class Main extends Component {
  
  state = {
    Book: []
  }
  

  componentDidMount = async() => {
   const res = await fetch('https://api.nytimes.com/svc/books/v3/lists/current/hardcover-fiction.json?api-key=WckKmFjAqaD4GEQqBioHzvp3EFxwa2TR')
    const data = await res.json();
    this.setState({ Book: data.results.books })
    
  }


    style = {
      width: "18rem"
    }

    
    render() { 
      
      
      console.log(this.state.Book[0]);
      return (
        <React.Fragment> {
        this.state.Book.map((id) => (
          <div className="card" style={this.style}>
          <img className="card-image-top" src={id.book_image} alt="Card image cap" />
          <div className="card-body">
            <h5 className="card-title">{id.title}</h5>
            <p className="card-text">{id.description}</p>
            <a href={id.amazon_product_url} className="btn btn-primary">Buy</a>
          </div>
          </div>
        )) }
        </React.Fragment>
      );
          
    }
}
 
export default Main;
